mapper.select:
SELECT * 
FROM cmt
WHERE movie_id = #{movieId}

	WHERE content LIKE CONCAT('%', #{search}, '%')

LIMIT #{startRow}, #{pageSize}

SELECT ifnull(sum(cmt_star), 0)
FROM cmt
WHERE movie_id= #{movieId}

mapper.update:
update cmt 
set cmt_content = #{cmtContent}, cmt_star = #{cmtStar} 
where cmt_number = #{cmtNumber}
